import '../App.scss';

function FilterTasks() {
    return (
        <div className="filter__sort">
            <h3 className="sort__title">Filter tasks by:</h3>
            <div className="sort__dropdown">
                <div className="dropdown__options">
                    <p className="option">Priority Low</p>
                    <p className="option">Priority Medium</p>
                    <p className="option">Priority High</p>
                    <p className="option">Deadline today</p>
                    <p className="option">Deadline this week</p>
                    <p className="option">Deadline this month</p>
                </div>
                <img className="dropdown__arrow" alt="arrow" src="/Images/arrow-up.svg"/>
            </div>
        </div>
    );
}

export default FilterTasks;
