import '../App.scss';
import TaskList from '../Components/TaskList.js';
import SortTasks from '../Components/SortTasks.js';
import FilterTasks from '../Components/FilterTasks.js';
import AddNewTask from '../Components/AddNewTask.js';

function HomePage() {
    return (
        <div class="home__page">
            <div class="page__header">
                <h1 class="header__title">Your Tasks</h1>
                <button class="button--generic">Add Task</button>
            </div>           
            <div class="page__filters">
                <FilterTasks/>
                <SortTasks/>
            </div>
            <div class="page__content">
                <TaskList className="open" />
                <TaskList className="in__progress" />
                <TaskList className="done" />
            </div>
        </div>
    );
}

export default HomePage;
