// Updated TaskList.js to fix issues with filteredTasks
import { useContext } from 'react';
import '../App.scss';
import TaskCard from './TaskCard.js';
import { TaskContext } from '../Context/TaskContext';

function TaskList({ status, filteredTasks }) {
    const { tasks } = useContext(TaskContext);

    // Ensure fallback to tasks of the correct status if filteredTasks is empty or undefined
    const displayedTasks = (filteredTasks && filteredTasks.length > 0)
        ? filteredTasks
        : tasks.filter((task) => task.status === status);

    return (
        <div className="task__list">
            <h2 className="list__title">
                {status === 'open' ? 'To Do' : status === 'in-progress' ? 'In Progress' : 'Done'}
            </h2>
            <div className="list__content">
                {displayedTasks.map((task) => (
                    <TaskCard key={task.taskId} {...task} />
                ))}
            </div>
        </div>
    );
}

export default TaskList;
