import '../App.scss';
import { useState, useContext } from 'react';
import { TaskContext } from '../Context/TaskContext';

function FilterTasks({ setFilteredTasks }) {
    const { tasks } = useContext(TaskContext); // Access tasks from context
    const [dropdownVisible, setDropdownVisible] = useState(false); // Toggle dropdown visibility
    const [activeFilter, setActiveFilter] = useState(null); // Track the active filter

    const toggleDropdown = () => {
        setDropdownVisible((prev) => !prev);
    };

    const handleFilter = (filter) => {
        setActiveFilter(filter);
        setDropdownVisible(false); // Close dropdown after selecting an option

        const now = new Date();
        const getStartOfWeek = (date) => {
            const start = new Date(date);
            const day = start.getDay() || 7;
            start.setHours(0, 0, 0, 0);
            start.setDate(start.getDate() - day + 1);
            return start;
        };

        const getStartOfMonth = (date) => {
            const start = new Date(date);
            start.setHours(0, 0, 0, 0);
            start.setDate(1);
            return start;
        };

        const openTasks = tasks.filter((task) => task.status === 'open'); // Filter by status

        const filtered = openTasks.filter((task) => {
            if (filter === 'Priority Low') return task.priority === 'low';
            if (filter === 'Priority Medium') return task.priority === 'mid';
            if (filter === 'Priority High') return task.priority === 'high';

            if (filter === 'Deadline today') {
                const deadline = new Date(task.deadline);
                return deadline.toDateString() === now.toDateString();
            }

            if (filter === 'Deadline this week') {
                const deadline = new Date(task.deadline);
                return (
                    deadline >= getStartOfWeek(now) &&
                    deadline <= new Date(getStartOfWeek(now).getTime() + 6 * 24 * 60 * 60 * 1000)
                );
            }

            if (filter === 'Deadline this month') {
                const deadline = new Date(task.deadline);
                return (
                    deadline >= getStartOfMonth(now) &&
                    deadline.getMonth() === now.getMonth()
                );
            }

            return true; // If no filter is active, show all tasks
        });

        setFilteredTasks(filtered);
    };

    return (
        <div className="filter__sort">
            <h3 className="sort__title">Filter tasks by:</h3>
            <div className="sort__dropdown">
                <div
                    className={`dropdown__options ${dropdownVisible ? 'visible' : 'hidden'
                        }`}
                >
                    {[
                        'Priority Low',
                        'Priority Medium',
                        'Priority High',
                        'Deadline today',
                        'Deadline this week',
                        'Deadline this month',
                    ].map((filter) => (
                        <p
                            key={filter}
                            className={`option ${activeFilter === filter ? 'active' : ''
                                }`}
                            onClick={() => handleFilter(filter)}
                        >
                            {filter}
                        </p>
                    ))}
                </div>
                <img
                    className={`dropdown__arrow ${dropdownVisible ? 'rotated' : ''
                        }`}
                    alt="arrow"
                    src="/Images/arrow-up.svg"
                    onClick={toggleDropdown}
                />
            </div>
        </div>
    );
}

export default FilterTasks;
