import React, { useState } from 'react';
import '../App.scss';
import TaskList from '../Components/TaskList';
import SortTasks from '../Components/SortTasks';
import FilterTasks from '../Components/FilterTasks';
import AddNewTask from '../Components/AddNewTask';

function HomePage({ filteredTasks, setFilteredTasks }) { // Accept props for filtering
    const [isAddNewTaskOpen, setIsAddNewTaskOpen] = useState(false);

    const toggleAddNewTask = () => {
        setIsAddNewTaskOpen(true); // Open the modal directly
    };

    const closeAddNewTask = () => {
        setIsAddNewTaskOpen(false); // Close the modal
    };

    return (
        <div className="home__page">
            <div className="page__header">
                <h1 className="header__title">Your Tasks</h1>
                <button className="button--generic" onClick={toggleAddNewTask}>
                    Add Task
                </button>
            </div>
            <div className="page__filters">
                {/* Pass setFilteredTasks to FilterTasks */}
                <FilterTasks setFilteredTasks={setFilteredTasks} />
                <SortTasks />
            </div>
            <div className="page__content">
                {/* Pass filteredTasks to the "open" TaskList */}
                <TaskList status="open" filteredTasks={filteredTasks} />
                {/* Unfiltered lists for "in-progress" and "done" */}
                <TaskList status="in-progress" />
                <TaskList status="done" />
            </div>
            {/* Add New Task modal */}
            {isAddNewTaskOpen && <AddNewTask closeModal={closeAddNewTask} />}
        </div>
    );
}

export default HomePage;
