import React, { useState } from "react";
import "./App.scss";
import FilterTasks from "./Components/FilterTasks";
import TaskList from "./Components/TaskList";

function App() {
    const [filteredTasks, setFilteredTasks] = useState(null); // State to hold filtered tasks

    return (
        <div className="app">
            <h1>Task Manager</h1>
            <FilterTasks setFilteredTasks={setFilteredTasks} /> {/* Pass setFilteredTasks */}
            <TaskList status="open" filteredTasks={filteredTasks} /> {/* Pass filteredTasks */}
        </div>
    );
}

export default App;
