import React, { createContext, useState } from 'react';

export const TaskContext = createContext();

export const TaskProvider = ({ children }) => {
    const [tasks, setTasks] = useState([]);

    const addTask = (newTask) => {
        setTasks((prevTasks) => {
            const updatedTasks = prevTasks.filter((task) => task.taskId !== newTask.taskId);
            const newTaskList = [...updatedTasks, newTask];
            console.log("Task added:", newTaskList);
            return newTaskList;
        });
    };

    const deleteTask = (taskId) => {
        setTasks((prevTasks) => {
            const updatedTasks = prevTasks.filter((task) => task.taskId !== taskId);
            console.log("Task deleted:", updatedTasks);
            return updatedTasks;
        });
    };

    const updateTaskStatus = (taskId, newStatus) => {
        setTasks((prevTasks) => {
            const updatedTasks = prevTasks.map((task) =>
                task.taskId === taskId
                    ? { ...task, status: newStatus }
                    : task
            );
            console.log("Task status updated:", updatedTasks);
            return [...updatedTasks];
        });
    };

    return (
        <TaskContext.Provider value={{ tasks, addTask, deleteTask, updateTaskStatus }}>
            {children}
        </TaskContext.Provider>
    );
};
