import '../App.scss';
import { useContext, useEffect } from 'react';
import TaskCard from './TaskCard.js';
import { TaskContext } from '../Context/TaskContext';

function TaskList({ status, filteredTasks, setFilteredTasks }) {
    const { tasks } = useContext(TaskContext);

    useEffect(() => {
        if (setFilteredTasks) {
            const updatedFilteredTasks = tasks.filter((task) => task.status === status);
            setFilteredTasks(updatedFilteredTasks);
        }
    }, [tasks, status, setFilteredTasks]);

    const displayedTasks = filteredTasks || tasks.filter((task) => task.status === status);

    return (
        <div className="task__list">
            <h2 className="list__title">
                {status === 'open' ? 'To Do' : status === 'in-progress' ? 'In Progress' : 'Done'}
            </h2>
            <div className="list__content">
                {displayedTasks.map((task) => (
                    <TaskCard
                        key={task.taskId}
                        {...task}
                    />
                ))}
            </div>
        </div>
    );
}

export default TaskList;
