import { useContext } from 'react';
import '../App.scss';
import { TaskContext } from '../Context/TaskContext';

function TaskCard({ taskId, taskName, priority, deadline, description, dateAdded }) {
    const { deleteTask } = useContext(TaskContext);

    const handleDelete = () => {
        deleteTask(taskId);
    };

    const handleDragStart = (event) => {
        console.log('Drag Start Event: Setting taskId', taskId);
        event.dataTransfer.setData('taskId', taskId);
    };

    return (
        <div
            className="task__card"
            draggable
            onDragStart={handleDragStart}
        >
            <div className="card__header">
                <h2 className="card__title">{taskName}</h2>
                <button className="card__button" onClick={handleDelete}></button>
            </div>
            <div className="card__content">
                <p>Priority: {priority}</p>
                <p>Deadline: {deadline}</p>
            </div>
        </div>
    );
}

export default TaskCard;
