import React, { useState, useContext } from 'react';
import { TaskContext } from '../Context/TaskContext'; // Correct import path
import './App.scss';

function AddNewTask() {
    // State for form inputs
    const [taskName, setTaskName] = useState('');
    const [priority, setPriority] = useState('');
    const [deadline, setDeadline] = useState('');
    const [description, setDescription] = useState('');
    const [isPopupOpen, setIsPopupOpen] = useState(true);

    // Access the addTask function from context
    const { addTask } = useContext(TaskContext);

    // Function to handle the form submit
    const handleSubmit = (e) => {
        e.preventDefault();

        const newTask = {
            taskName,
            priority,
            deadline,
            description,
        };

        addTask(newTask); // Submit the data to context

        // After submitting, clear the form data and keep the popup open (optional)
        setTaskName('');
        setPriority('');
        setDeadline('');
        setDescription('');
    };

    // Function to close the popup and clear the form
    const handleClose = () => {
        setIsPopupOpen(false); // Close the popup
        setTaskName('');
        setPriority('');
        setDeadline('');
        setDescription('');
    };

    return (
        <>
            {isPopupOpen && (
                <div className="popup__cover">
                    <div className="task__add">
                        <form className="task__card" onSubmit={handleSubmit}>
                            <div className="card__header">
                                <div className="input__field">
                                    <label className="text__label" htmlFor="task-name">
                                        <img alt="pen" src="./Images/pen.svg" />
                                    </label>
                                    <input
                                        className="text__field title"
                                        type="text"
                                        id="task-name"
                                        name="task-name"
                                        value={taskName} // Controlled input
                                        onChange={(e) => setTaskName(e.target.value)} // Update state on change
                                        required
                                    />
                                </div>
                                <button
                                    className="card__button"
                                    type="button"
                                    onClick={handleClose} // Close the popup
                                >
                                    Close
                                </button>
                            </div>

                            <div className="card__content">
                                <div className="input__field">
                                    <p className="text__label date">Priority:</p>
                                    <input
                                        className="radio"
                                        type="radio"
                                        id="high"
                                        name="priority"
                                        value="high"
                                        checked={priority === 'high'}
                                        onChange={(e) => setPriority(e.target.value)}
                                    />
                                    <label className="radio__label" htmlFor="high">
                                        High
                                    </label>
                                    <input
                                        className="radio"
                                        type="radio"
                                        id="mid"
                                        name="priority"
                                        value="mid"
                                        checked={priority === 'mid'}
                                        onChange={(e) => setPriority(e.target.value)}
                                    />
                                    <label className="radio__label" htmlFor="mid">
                                        Medium
                                    </label>
                                    <input
                                        className="radio"
                                        type="radio"
                                        id="low"
                                        name="priority"
                                        value="low"
                                        checked={priority === 'low'}
                                        onChange={(e) => setPriority(e.target.value)}
                                    />
                                    <label className="radio__label" htmlFor="low">
                                        Low
                                    </label>
                                </div>

                                <div className="date__field">
                                    <div className="input__field">
                                        <label className="text__label date" htmlFor="deadline">
                                            Deadline:
                                        </label>
                                        <input
                                            className="text__field"
                                            type="date"
                                            id="deadline"
                                            name="deadline"
                                            value={deadline}
                                            onChange={(e) => setDeadline(e.target.value)}
                                        />
                                    </div>
                                    <div className="input__field">
                                        <label className="text__label date" htmlFor="date-added">
                                            Date added:
                                        </label>
                                        <input
                                            className="text__field"
                                            type="date"
                                            id="date-added"
                                            name="date-added"
                                            value={description}
                                            onChange={(e) => setDescription(e.target.value)}
                                        />
                                    </div>
                                </div>

                                <div className="input__field">
                                    <label className="text__label" htmlFor="task-description">
                                        <img alt="pen" src="./Images/pen.svg" />
                                    </label>
                                    <textarea
                                        className="text__field description"
                                        id="task-description"
                                        name="task-description"
                                        value={description}
                                        onChange={(e) => setDescription(e.target.value)}
                                        rows="10"
                                        cols="44.5"
                                    ></textarea>
                                </div>

                                <input
                                    className="button--generic"
                                    type="submit"
                                    value="Add Task"
                                />
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </>
    );
}

export default AddNewTask;
