/* TaskList.js with Enhanced Debugging for Drop Events */
import '../App.scss';
import { useContext, useEffect, useState } from 'react';
import TaskCard from './TaskCard.js';
import { TaskContext } from '../Context/TaskContext';

function TaskList({ status, filteredTasks, setFilteredTasks }) {
    const { tasks, updateTaskStatus } = useContext(TaskContext);
    const [localTasks, setLocalTasks] = useState([]);

    useEffect(() => {
        // Filter tasks by status and update local state
        const updatedTasks = tasks.filter((task) => task.status === status);
        setLocalTasks(updatedTasks);

        // If a parent component wants to manage filtered tasks, update it as well
        if (setFilteredTasks) {
            setFilteredTasks(updatedTasks);
        }
    }, [tasks, status, setFilteredTasks]);

    const handleDragOver = (event) => {
        event.preventDefault();
        console.log("Drag over detected on list with status:", status);
    };

    const handleDrop = (event) => {
        event.preventDefault();
        console.log("Drop event detected on list with status:", status);
        const taskId = event.dataTransfer.getData('taskId');
        console.log("Task ID retrieved from drop event:", taskId);
        const draggedTask = tasks.find((task) => task.taskId === taskId);

        if (draggedTask) {
            console.log(`Task ${taskId} - Initial Status: ${draggedTask.status}`);

            // Update the task status only if it's dropped in a different list
            if (draggedTask.status !== status) {
                updateTaskStatus(taskId, status);
                console.log(`Task ${taskId} - Updated Status: ${status}`);
            }
        } else {
            console.log("No task found for the given Task ID:", taskId);
        }
    };

    return (
        <div
            className="task__list"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
        >
            <h2 className="list__title">
                {status === 'open' ? 'To Do' : status === 'in-progress' ? 'In Progress' : 'Done'}
            </h2>
            <div className="list__content">
                {localTasks.map((task) => (
                    <TaskCard
                        key={task.taskId} // Unique key for React rendering
                        {...task} // Spread all task properties as props
                        draggable="true"
                        onDragStart={(event) => {
                            console.log("Drag started for task:", task.taskId);
                            event.dataTransfer.setData('taskId', task.taskId);
                        }}
                    />
                ))}
            </div>
        </div>
    );
}

export default TaskList;
