import '../App.scss';
import { useContext, useEffect } from 'react';
import TaskCard from './TaskCard.js';
import { TaskContext } from '../Context/TaskContext';

function TaskList({ status, filteredTasks, setFilteredTasks }) {
    const { tasks, updateTaskStatus } = useContext(TaskContext);

    useEffect(() => {
        if (setFilteredTasks) {
            const updatedFilteredTasks = tasks.filter((task) => task.status === status);
            setFilteredTasks(updatedFilteredTasks);
        }
    }, [tasks, status, setFilteredTasks]);

    const displayedTasks = filteredTasks || tasks.filter((task) => task.status === status);

    // Drag and Drop Handlers
    const handleDragStart = (event, taskId) => {
        event.dataTransfer.setData('taskId', taskId);
    };

    const handleDragOver = (event) => {
        event.preventDefault();
    };

    const handleDrop = (event) => {
        event.preventDefault();
        const taskId = event.dataTransfer.getData('taskId');
        updateTaskStatus(taskId, status); // Update task status when dropped
    };

    return (
        <div
            className="task__list"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
        >
            <h2 className="list__title">
                {status === 'open' ? 'To Do' : status === 'in-progress' ? 'In Progress' : 'Done'}
            </h2>
            <div className="list__content">
                {displayedTasks.map((task) => (
                    <TaskCard
                        key={task.taskId}
                        {...task}
                        draggable
                        onDragStart={(event) => handleDragStart(event, task.taskId)}
                    />
                ))}
            </div>
        </div>
    );
}

export default TaskList;
